//move page left-right------------------------------------------------
function adjustPage(p, d) {
    if (p < 0 && d == 1) {
        p = numPages - 1;
    } else if (p >= numPages - 1 && d == 1) {
        numPage = 0;
        p = numPages - 1;
    } else if (p <= 0 && d == -1) {
        numPage = numPages - 1;
        p = 0;
    } else if (p == numPages && d == -1) {
        p = 0;
    }
    return p;
}
