function ImgLoaderPreload(img, page) {
    if (!imgLibrary[img]) {
        var rt = new Image();
        rt.crossOrigin = 'crossdomain.xml';
        rt.src = proxy + img;
        rt.onload = function() {
            imgLibrary[img] = rt;
            if (bookPreload) {
                books[page]["img"] = rt;
                loadBook(page + 1);
            } else {
                element += 1;
                PreloadImg(page);
            }
        }
        rt.onerror = function() {
            imgLibrary[img] = common.toolImages.errorImg[1];
            if (bookPreload) {
                books[page]["img"] = common.toolImages.avatarImg[1];
                loadBook(page + 1);
            } else {
                element += 1;
                PreloadImg(page);
            }
        }

    } else {
        if (bookPreload) {
            books[page]["img"] = imgLibrary[img];
            loadBook(page + 1);
            writeLoadStatus(page);
        } else {
            element += 1;
            PreloadImg(page);
        }
    }
}
