//write multiline
function writeInfoMultiline(info, x, y) {
    var txt = info;
    var start = 0; // Carï¿½cter por el que empezar
    var currentLine = 0; // Lï¿½nea en la que nos encontramos.
    var jumpAt = 200; // Nï¿½mero de caracteres por lï¿½nea.
    var lineHeight = 20; // Ancho de cada lï¿½nea.
    var str; // Auxiliar donde guardar el texto partido.
    context.font = "normal 9px Georgia ";
    context.fillStyle = "#ffffff";
    context.textBaseline = "top";
    while (start < txt.length)
    {
        str = info.substr(start, jumpAt);
        start += jumpAt;
        context.fillText(str, x, y + (lineHeight * currentLine++));
    }
}
