//keyboard
document.onkeypress = function(e) {

    e = e || event;
    if (!formActiv) {
        delForms();
        var chr = getChar(e);
    }
    switch (chr) {
        case ("m"):
            if (MODE == "VISOR") {
                navigationMode = !navigationMode;
                createMaidIni(pageFocus, 0, 0);
            }
            break;
        case ("e"):
            if (permission != 2) {
                if (MODE == "VISOR") {
                    cleanVideos();
                    toEDITOR();
                    createMaidIni(pageFocus, 0, 0);
                    context.drawImage(common.toolImages.recordPB[1], 0, areaH - 80);
                } else {
                    toMAIN();
                }
            }
            break;
        case ("b"):
            cleanVideos();
            if (permission != 2) {
                delForms();
                if (SUBMODE != "BOOKS") {
                    if (numBooks == 0) {
                        alert("You do not have edited blocs");
                    } else {
                        endMove = false;
                        cleanVideos();
                        //go books
                        if (numBooks == 0) {
                            alert("You do not have edited blocs");
                        } else {
                            Offsset = 100;
                            tween(theCanvas, "fade", {alpha: 0}, velFade, toBOOKS, Quad_easeInOut, "VISOR", execCreateMaidIni);
                        }
                    }
                } else {
                    OLDSUBMODE = "MAIN";
                    SUBMODE = "MAIN";
                    posBookX = 0;
                    posBookXAv = 0;
                    clearInterval(showPagesTween);
                    if (createMode) {
                        createMode();
                        createMode = null;
                    } else {
                        createMaidIni(pageFocus, posBookX, 0);
                    }
                    toMAIN();
                }
            }
            break;
        case ("p"):
            cleanVideos();
            if (SUBMODE != "PAGES") {
                toPAGES();
                showPages();
            } else {
                clearInterval(showPagesTween);
                posBookX = 200;
                posBookXAv = 200;
                endMove = false;
                showPagesTween = setInterval(hidePages, 33);
                toMAIN();
            }
            break;
        case ("r"):
            refreshPage();
            break;
        case ("c"):
            if (permission != 2) {
                if (maidSelect) {
                    directEditor(copyMaid);
                } else {
                    alert("No Maid Selected to Copy");
                }
            }
            break;
        case ("v"):
            if (permission != 2) {
                if (maidCopy) {
                    directEditor(pasteMaid);
                } else {
                    alert("No Maid to Paste");
                }
                break;
            }
        case ("d"):
            if (permission != 2) {
                if (maidSelect) {
                    directEditor(duplicateMaid);
                } else {
                    alert("No Maid Selected to Duplicate");
                }
            }
            break;
        case ("x"):
            if (permission != 2) {
                if (maidSelect) {
                    directEditor(cutMaid);
                } else {
                    alert("No Maid Selected to Cut");
                }
            }
            break;
        case ("s"):
            if (permission == 1) {
                recordPage();
            } else if (permission != 2) {
                alert("No te podemos dejar grabar desde aquï¿½, esto es una demo!!!!!");
            }
            break;
        case ("a"):
            dragItemNum = Maids[pageFocus].indexOf(maidSelect);
            if (dragItemNum > 0) {
                swapItems(dragItemNum - 1);
                createMaidIni(pageFocus, 0, 0);
            }
            break;
        case ("q"):
            dragItemNum = Maids[pageFocus].indexOf(maidSelect);
            if (dragItemNum < Maids[pageFocus].length - 1) {
                swapItems(dragItemNum + 1);
                createMaidIni(pageFocus, 0, 0);
            }
            break;
        default:
            break;
    }
}
function getChar(event) {
    if (event.charCode == 39 || event.charCode == 63233) {
        cleanVideos();
        maidSelect = null;
        detect = null;
        numPage += 1;
        if (numPage > numPages - 1) {
            numPage = 0;
        }

        direction = 1;
        backPage = null;
        endMove = false;
        pageXvar = areaW;
        pageX = 0;
        resetParamsPage();
        BGImgH = 0;
        /*clearInterval(movPage);
         //removeListeners();
         movPage=setInterval(drawLeft,velTween);*/
        endMove = false;
        Offsset = areaW;
        adjustPageStatic = numPage - pageFocus - 1;
        createMaidIni(numPage, areaW, 0);
        tween(theCanvas, "avPage", {x: 0}, velMove, endMovePage, Quad_easeOut, MODE, null);
    }
    if (event.charCode == 37 || event.charCode == 63232) {
        cleanVideos();
        maidSelect = null;
        detect = null;
        numPage -= 1;
        if (numPage < 0) {
            numPage = numPages - 1;
        }
        direction = -1;
        backPage = null;
        pageXvar = areaW;
        pageX = 0;
        resetParamsPage();
        BGImgH = 0;
        /*clearInterval(movPage);
         removeListeners();
         movPage=setInterval(drawRight,velTween);*/
        Offsset = 0;
        endMove = false;
        adjustPageStatic = numPage - pageFocus + 1;
        if (Math.abs(adjustPageStatic) >= numPages - 1) {
            adjustPageStatic = 0;
        }
        createMaidIni(numPage, -areaW, 0);
        tween(theCanvas, "rePage", {x: areaW}, velMove, endMovePage, Quad_easeOut, MODE, null);
    }
    if (event.which == null) {
        return String.fromCharCode(event.keyCode) // IE
    } else if (event.which != 0 && event.charCode != 0) {
        return String.fromCharCode(event.which)   // the rest
    } else {
        return null // special key
    }
}
